mapper.select:
SELECT COUNT(*)
FROM USER_TBL
WHERE user_seq = #{userSeq} AND password = #{currentPassword}

SELECT user_seq, id as userId, password, nickname, profile_image as profileImage
FROM USER_TBL
WHERE user_seq = #{userSeq}

SELECT * FROM (
    SELECT a.*, ROWNUM AS rnum
    FROM (
        SELECT *
        FROM POST_TBL
        WHERE user_seq = #{userSeq}
        ORDER BY created_at DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rnum > #{offset}

SELECT * FROM (
    SELECT a.*, ROWNUM AS rnum
    FROM (
        SELECT *
        FROM COMMENT_TBL
        WHERE user_seq = #{userSeq}
        ORDER BY created_at DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{limit}
    )
WHERE rnum > #{offset}

SELECT * FROM (
    SELECT a.*, ROWNUM AS rnum
    FROM (
        SELECT p.*
        FROM FAVORITE_TBL f
        JOIN POST_TBL p ON f.post_seq = p.post_seq
        WHERE f.user_seq = #{userSeq}
        ORDER BY f.favorite_seq DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{limit}
    )
WHERE rnum > #{offset}

SELECT * FROM (
    SELECT a.*, ROWNUM AS rnum
    FROM (
        SELECT p.*
        FROM LIKE_TBL l
        JOIN POST_TBL p ON l.post_seq = p.post_seq
        WHERE l.user_seq = #{userSeq}
        ORDER BY l.like_seq DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{limit}
    )
WHERE rnum > #{offset}
